      }
      SCX_CATCH (CORBA::Exception, e)
      {
         cerr << e._rep_id() << endl;
      }
      SCX_END_TRY
   }
   SCX_CATCH (CORBA::Exception, e)
   {
      cerr << e._rep_id() << endl;
   }
   SCX_END_TRY
}

